import React from 'react';
import { useNavigate } from 'react-router-dom';

const services = [
  { id: 1, title: "Web Development", desc: "Xây dựng website hiện đại, responsive." },
  { id: 2, title: "Mobile App Development", desc: "Ứng dụng di động cho iOS & Android." },
  { id: 3, title: "UI/UX Design", desc: "Thiết kế giao diện người dùng đẹp mắt." },
  { id: 4, title: "Backend API", desc: "Xây dựng hệ thống API mạnh mẽ, bảo mật." }
];

function Home() {
  const navigate = useNavigate();
  const isLoggedIn = localStorage.getItem("currentUser");

  const handleViewDetail = (service) => {
    if (isLoggedIn) {
      alert(`Chi tiết dịch vụ: ${service.title}\n\n${service.desc}`);
    } else {
      alert("Vui lòng đăng nhập để xem chi tiết dịch vụ.");
      navigate("/login");
    }
  };

  return (
    <div className="container">
      <h2 className="text-center mb-4">Dịch vụ Dev as a Service</h2>
      <div className="row">
        {services.map(service => (
          <div className="col-md-6 mb-4" key={service.id}>
            <div className="card">
              <div className="card-body">
                <h5 className="card-title">{service.title}</h5>
                <p className="card-text">{service.desc}</p>
                <button className="btn btn-primary" onClick={() => handleViewDetail(service)}>Xem chi tiết</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
