import React from 'react';
import { useNavigate } from 'react-router-dom';

const services = [
  {
    id: 1,
    title: "Web Development",
    desc: "Xây dựng website chuẩn SEO, hiệu suất cao với công nghệ hiện đại như React, Next.js. Tối ưu trải nghiệm người dùng và tốc độ tải trang."
  },
  {
    id: 2,
    title: "Mobile App Development",
    desc: "Phát triển ứng dụng Android/iOS native hoặc cross-platform bằng Flutter, React Native. Giao diện mượt mà, hiệu năng cao."
  },
  {
    id: 3,
    title: "UI/UX Design",
    desc: "Thiết kế trải nghiệm người dùng (UX) trực quan, hiện đại. Giao diện (UI) sáng tạo, dễ dùng, phù hợp thương hiệu."
  },
  {
    id: 4,
    title: "Backend API",
    desc: "Xây dựng hệ thống backend bảo mật cao, RESTful API mạnh mẽ bằng Node.js, Express, MongoDB hoặc PostgreSQL."
  }
];

function Home() {
  const navigate = useNavigate();
  const storedUser = JSON.parse(localStorage.getItem("currentUser"));
  const isLoggedIn = !!storedUser;

  const handleViewDetail = (service) => {
    if (isLoggedIn) {
      alert(`Chi tiết dịch vụ: ${service.title}\n\n${service.desc}`);
    } else {
      alert("Vui lòng đăng nhập để xem chi tiết dịch vụ.");
      navigate("/login");
    }
  };

  return (
    <div className="container">
      <section className="text-center my-5">
        <h2>Giới thiệu về ADS DEV</h2>
        <p>
          ADS DEV là công ty chuyên cung cấp dịch vụ phát triển phần mềm và giải pháp quảng cáo toàn diện.
          Chúng tôi tập trung vào công nghệ hiện đại, trải nghiệm người dùng và hiệu quả kinh doanh cho khách hàng.
        </p>
      </section>

      <h3 className="text-center mb-4">Dịch vụ Dev as a Service</h3>
      <div className="row">
        {services.map(service => (
          <div className="col-12 col-sm-6 col-lg-4 mb-4" key={service.id}>
            <div className="card h-100 shadow-sm">
              <div className="card-body d-flex flex-column">
                <h5 className="card-title">{service.title}</h5>
                <p className="card-text flex-grow-1">{service.desc}</p>
                <button className="btn btn-primary mt-2" onClick={() => handleViewDetail(service)}>Xem chi tiết</button>
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
