import React from 'react';

const recommended = [1,2,3].map(i => ({
  name: `Guitar Acoustic ${i}`,
  price: '4.500.000đ',
  img: 'https://images.unsplash.com/photo-1511671782779-c97d3d27a1d4?auto=format&fit=crop&w=400&q=80',
}));

function Dashboard({ user }) {
  return (
    <div className="container mt-4">
      <div style={{ background: '#f8f9fa', borderRadius: 16, padding: 32, textAlign: 'center', marginBottom: 32 }}>
        <h2>Chào mừng {user?.name || user?.email}!</h2>
        <p>Chúng tôi sẵn sàng hỗ trợ bạn trong mọi giải pháp âm nhạc.</p>
      </div>
      <h3 className="mb-3">Sản phẩm gợi ý cho bạn</h3>
      <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(220px, 1fr))', gap: 24 }}>
        {recommended.map((p, idx) => (
          <div key={idx} style={{ background: '#fff', borderRadius: 12, boxShadow: '0 2px 12px rgba(0,0,0,0.06)', padding: 16, textAlign: 'center' }}>
            <img src={p.img} alt={p.name} style={{ width: '100%', height: 120, objectFit: 'cover', borderRadius: 8, marginBottom: 8 }} />
            <div style={{ fontWeight: 600 }}>{p.name}</div>
            <div style={{ color: '#0d8abc', fontWeight: 700, margin: '8px 0' }}>{p.price}</div>
            <button className="btn btn-primary btn-sm">Xem chi tiết</button>
          </div>
        ))}
      </div>
      <div style={{ margin: '48px 0 16px 0', background: '#f8f9fa', borderRadius: 12, padding: 32, textAlign: 'center' }}>
        <h4>Lịch sử mua hàng (mẫu)</h4>
        <div>Chưa có đơn hàng nào.</div>
      </div>
    </div>
  );
}

export default Dashboard;
